// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.8.7;

import {IDistributor} from "../distributor/IDistributor.sol";

interface IPresale {
    function tokenInfo() external returns (address, string memory, string memory, uint256);
    function getProgress() external view returns (uint256);
    function getRaisedAmount() external view returns (uint256);
    function isBondingCurveEnd() external view returns (bool);
    function distribute(address distributor, uint256 deadline) external;
    function canDistribute(address distributor) external view returns (bool);
    function info() external view returns (PresaleInfo memory);
    function isEnd() external view returns (bool);
    function isExpired() external view returns (bool);

    struct PresaleInfo {
        address minter;
        address token;
        address pool;

        address paymentToken;
        uint256 amountToRaise;

        uint256 amountToSale;
        string data;

        // 1e6 => 100%;
        uint24 toTreasuryRate;

        bool isEnd;
        uint256 startTimestamp;
        bool isNewToken;
    }

    event Distributed();
}

contracts/presale/Presale.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.8.7;

import {CommonToken} from "@kayen/token/contracts/CommonToken.sol";

import "./IPresale.sol";
import {ERC721Receiver} from "../libraries/ERC721Receiver.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import {IDistributor} from "../distributor/IDistributor.sol";
import {Configuration} from "../Configuration.sol";

abstract contract Presale is IPresale, ERC721Receiver, Initializable {
    using SafeERC20 for ERC20;

    PresaleInfo internal _info;
    Configuration internal _config;

    function _initialize(Configuration config, PresaleInfo memory info) internal initializer {
        _info = info;
        _config = config;
    }

    function tokenInfo() public virtual returns (address, string memory, string memory, uint256) {
        ERC20 tokenInstance = ERC20(_info.token);
        return (_info.token, tokenInstance.name(), tokenInstance.symbol(), tokenInstance.totalSupply());
    }

    function isEnd() external view override returns (bool) {
        return _info.isEnd;
    }

    function info() public view override returns (PresaleInfo memory) {
        return _info;
    }

    function getProgress() public view virtual returns (uint256);

    function isBondingCurveEnd() public view returns (bool) {
        return getProgress() >= 1e6;
    }

    function isExpired() public view override returns (bool) {
        return block.timestamp > _info.startTimestamp + _config.maxPresaleDuration();
    }

    function _beforeDistribute(uint256 deadline) internal virtual returns (address, address, uint160);

    function canDistribute(address distributor) external view override returns (bool) {
        return isBondingCurveEnd() && IDistributor(distributor).canDistribute(_info.token, _info.paymentToken);
    }

    function distribute(address distributor, uint256 deadline) external override {
        require(
            distributor == _config.defaultDistributor() || msg.sender == _info.minter || msg.sender == _config.owner(),
            "Presale: not authorized"
        );
        require(isBondingCurveEnd() || isExpired(), "Presale: bonding curve not end");
        require(_config.isDistributorWhitelisted(distributor), "Presale: distributor not whitelisted");

        (address token0, address token1, uint160 sqrtPriceX96) = _beforeDistribute(deadline);
        uint256 balance0 = ERC20(token0).balanceOf(address(this));
        uint256 balance1 = ERC20(token1).balanceOf(address(this));

        sendDistributionFee(token0, token1, balance0, balance1);
        sendToTreasury(token0, token1, balance0, balance1);

        if (_info.isNewToken) {
            CommonToken(_info.token).removeBlacklist();
            ERC20(token0).safeTransfer(distributor, ERC20(token0).balanceOf(address(this)));
            ERC20(token1).safeTransfer(distributor, ERC20(token1).balanceOf(address(this)));
            IDistributor(distributor).distribute(token0, token1, sqrtPriceX96, deadline);
        } else {
            ERC20(token0).safeTransfer(_info.minter, ERC20(token0).balanceOf(address(this)));
            ERC20(token1).safeTransfer(_info.minter, ERC20(token1).balanceOf(address(this)));
        }

        _info.isEnd = true;

        emit Distributed();
    }

    function sendToTreasury(address token0, address token1, uint256 balance0, uint256 balance1) internal {
        if (_info.toTreasuryRate == 0) {
            return;
        }
        uint256 token0Amount = (balance0 * _info.toTreasuryRate) / 1e6;
        uint256 token1Amount = (balance1 * _info.toTreasuryRate) / 1e6;
        if (token0Amount > 0) {
            ERC20(token0).safeTransfer(_info.minter, token0Amount);
        }
        if (token1Amount > 0) {
            ERC20(token1).safeTransfer(_info.minter, token1Amount);
        }
    }

    function sendDistributionFee(address token0, address token1, uint256 balance0, uint256 balance1) internal {
        uint256 rate = _config.getDistributionFeeRate(_info.token, _info.paymentToken);
        if (rate == 0) {
            return;
        }
        if (balance0 > 0) {
            uint256 amount1 = (balance0 * rate) / 1e6;
            ERC20(token0).safeTransfer(_config.feeVault(), amount1);
        }

        if (balance1 > 0) {
            uint256 amount2 = (balance1 * rate) / 1e6;
            ERC20(token1).safeTransfer(_config.feeVault(), amount2);
        }
    }
}

contracts/presale/UniswapV3Presale.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.8.7;

import {INonfungiblePositionManager} from "@kayen/uniswap-v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol";
import {PositionValue} from "@kayen/uniswap-v3-periphery/contracts/libraries/PositionValue.sol";
import {IUniswapV3Pool} from "@kayen/uniswap-v3-core/contracts/interfaces/IUniswapV3Pool.sol";

import "./Presale.sol";
import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IDistributor} from "../distributor/IDistributor.sol";
import {Configuration} from "../Configuration.sol";
import {ISwapRouter} from "@kayen/uniswap-v3-periphery/contracts/interfaces/ISwapRouter.sol";
import {IQuoter} from "@kayen/uniswap-v3-periphery/contracts/interfaces/IQuoter.sol";

contract UniswapV3Presale is Presale {
    INonfungiblePositionManager positionManager;
    ISwapRouter private swapRouter;
    IQuoter private quoter;

    function initialize(
        ISwapRouter _swapRouter,
        INonfungiblePositionManager _positionManager,
        IQuoter _quoter,
        PresaleInfo memory info,
        Configuration config
    ) external {
        super._initialize(config, info);
        swapRouter = _swapRouter;
        positionManager = _positionManager;
        quoter = _quoter;
    }

    function getProgress() public view override returns (uint256) {
        PositionInfo memory positionInfo = getPositionInfo();
        uint256 raisedAmount = getRaisedAmount();
        return (1e6 * raisedAmount) / _info.amountToRaise;
    }

    function getTokenId() public view returns (uint256) {
        return positionManager.tokenOfOwnerByIndex(address(this), 0);
    }

    function getRaisedAmount() public view override returns (uint256) {
        PositionInfo memory positionInfo = getPositionInfo();
        uint256 tokenId = getTokenId();
        (uint160 sqrtPriceX96, , , , , , ) = IUniswapV3Pool(_info.pool).slot0();
        (uint256 reserve0, uint256 reserve1) = PositionValue.total(positionManager, tokenId, sqrtPriceX96);
        if (positionInfo.token0 != _info.token) {
            return reserve0;
        } else {
            return reserve1;
        }
    }

    function _beforeDistribute(uint256 deadline) internal override returns (address, address, uint160) {
        PositionInfo memory positionInfo = getPositionInfo();
        (uint160 sqrtPriceX96, , , , , , ) = IUniswapV3Pool(_info.pool).slot0();
        burnPosition(deadline);
        return (positionInfo.token0, positionInfo.token1, sqrtPriceX96);
    }

    function burnPosition(uint256 deadline) internal {
        PositionInfo memory position = getPositionInfo();
        uint256 tokenId = getTokenId();
        positionManager.decreaseLiquidity(
            INonfungiblePositionManager.DecreaseLiquidityParams(tokenId, position.liquidity, 0, 0, deadline)
        );
        positionManager.collect(
            INonfungiblePositionManager.CollectParams({
                tokenId: tokenId,
                recipient: address(this),
                amount0Max: type(uint128).max,
                amount1Max: type(uint128).max
            })
        );
        positionManager.burn(tokenId);
    }

    function getPositionInfo() public view returns (PositionInfo memory) {
        uint256 tokenId = getTokenId();
        (
            uint96 nonce,
            address operator,
            address token0,
            address token1,
            uint24 fee,
            int24 tickLower,
            int24 tickUpper,
            uint128 liquidity,
            uint256 feeGrowthInside0LastX128,
            uint256 feeGrowthInside1LastX128,
            uint128 tokensOwed0,
            uint128 tokensOwed1
        ) = positionManager.positions(tokenId);
        return
            PositionInfo(
                nonce,
                operator,
                token0,
                token1,
                fee,
                tickLower,
                tickUpper,
                liquidity,
                feeGrowthInside0LastX128,
                feeGrowthInside1LastX128,
                tokensOwed0,
                tokensOwed1
            );
    }

    struct PositionInfo {
        uint96 nonce;
        address operator;
        address token0;
        address token1;
        uint24 fee;
        int24 tickLower;
        int24 tickUpper;
        uint128 liquidity;
        uint256 feeGrowthInside0LastX128;
        uint256 feeGrowthInside1LastX128;
        uint128 tokensOwed0;
        uint128 tokensOwed1;
    }
}

contracts/presale/UniswapV3PresaleMaker..content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.8.7;

import "../Configuration.sol";
import "./IPresale.sol";
import "@kayen/token/contracts/TokenFactory.sol";
import "@kayen/token/contracts/CommonToken.sol";

import {Quoter} from "@kayen/uniswap-v3-periphery/contracts/lens/Quoter.sol";

import {PresaleManager} from "../presale-manager/PresaleManager.sol";
import {UniswapV3Presale} from "./UniswapV3Presale.sol";
import {ERC721Receiver} from "../libraries/ERC721Receiver.sol";

import {ISwapRouter} from "@kayen/uniswap-v3-periphery/contracts/interfaces/ISwapRouter.sol";
import {LiquidityAmounts} from "@kayen/uniswap-v3-periphery/contracts/libraries/LiquidityAmounts.sol";
import {TickMath} from "@kayen/uniswap-v3-core/contracts/libraries/TickMath.sol";
import {IUniswapV3Pool} from "@kayen/uniswap-v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {INonfungiblePositionManager} from "@kayen/uniswap-v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol";
import {IUniswapV3Factory} from "@kayen/uniswap-v3-core/contracts/interfaces/IUniswapV3Factory.sol";
import {ERC1967Proxy} from "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";

contract UniswapV3PresaleMaker is ERC721Receiver {
    using SafeERC20 for IERC20;

    Configuration public immutable config;
    TokenFactory public immutable tokenFactory;
    UniswapV3Presale public immutable implementation;
    IUniswapV3Factory public immutable poolFactory;
    INonfungiblePositionManager public immutable positionManager;
    PresaleManager public immutable presaleManager;
    ISwapRouter public immutable swapRouter;
    Quoter public immutable quoter;

    uint24 public immutable poolFee = 100;
    address public constant eth = address(0);
    address public immutable weth;

    constructor(
        Configuration _config,
        PresaleManager _presaleManager,
        TokenFactory _tokenFactory,
        IUniswapV3Factory _poolFactory,
        INonfungiblePositionManager _positionManager,
        ISwapRouter _swapRouter,
        Quoter _quoter,
        address _weth
    ) {
        config = _config;
        tokenFactory = _tokenFactory;
        poolFactory = _poolFactory;
        positionManager = _positionManager;
        presaleManager = _presaleManager;
        implementation = new UniswapV3Presale();
        swapRouter = _swapRouter;
        weth = _weth;
        quoter = _quoter;
    }

    struct NewTokenPresaleParams {
        address paymentToken;
        string name;
        string symbol;
        uint256 totalSupply;
        uint160 sqrtPriceX96;
        int24 tickLower;
        int24 tickUpper;
        uint256 amountToSale;
        uint256 amountToRaise;
        uint256 amountForBuyInstantly;
        uint24 toTreasuryRate;
        uint256 startTimestamp;
        uint256 deadline;
        string data;
    }

    struct TokenPresaleParams {
        address paymentToken;
        address saleToken;
        uint160 sqrtPriceX96;
        int24 tickLower;
        int24 tickUpper;
        uint256 amountToSale;
        uint256 amountToRaise;
        uint256 amountForBuyInstantly;
        uint24 toTreasuryRate;
        uint256 startTimestamp;
        uint256 deadline;
        string data;
    }

    function startWithNewTokenWithParams(
        NewTokenPresaleParams memory params,
        address minter
    ) public payable returns (IPresale) {
        return
            startWithNewToken(
                minter,
                params.paymentToken,
                params.name,
                params.symbol,
                params.totalSupply,
                params.sqrtPriceX96,
                params.tickLower,
                params.tickUpper,
                params.amountToSale,
                params.amountToRaise,
                params.amountForBuyInstantly,
                params.toTreasuryRate,
                params.startTimestamp,
                params.deadline,
                params.data
            );
    }

    function startWithParams(TokenPresaleParams memory params, address minter) public payable returns (IPresale) {
        return
            start(
                minter,
                params.paymentToken,
                params.saleToken,
                params.sqrtPriceX96,
                params.tickLower,
                params.tickUpper,
                params.amountToSale,
                params.amountToRaise,
                params.amountForBuyInstantly,
                params.toTreasuryRate,
                params.startTimestamp,
                params.deadline,
                params.data
            );
    }

    function startWithNewToken(
        address minter,
        address paymentToken,
        string memory name,
        string memory symbol,
        uint256 totalSupply,
        uint160 sqrtPriceX96,
        int24 tickLower,
        int24 tickUpper,
        uint256 amountToSale,
        uint256 amountToRaise,
        uint256 amountForBuyInstantly,
        uint24 toTreasuryRate,
        uint256 startTimestamp,
        uint256 deadline,
        string memory data
    ) public payable returns (IPresale) {
        address token = tokenFactory.create(name, symbol, totalSupply);
        CommonToken tokenInstance = CommonToken(token);
        tokenInstance.putBlacklist(config);
        uint256 minterAllocation = totalSupply - amountToSale;
        if (minterAllocation > 0) {
            tokenInstance.transfer(minter, minterAllocation);
        }
        config.putComputedTransferBlacklist(paymentToken == eth ? weth : paymentToken, token);
        return
            _create(
                minter,
                true,
                paymentToken,
                token,
                sqrtPriceX96,
                tickLower,
                tickUpper,
                amountToSale,
                amountToRaise,
                amountForBuyInstantly,
                toTreasuryRate,
                startTimestamp,
                deadline,
                data
            );
    }

    function start(
        address minter,
        address paymentToken,
        address saleToken,
        uint160 sqrtPriceX96,
        int24 tickLower,
        int24 tickUpper,
        uint256 amountToSale,
        uint256 amountToRaise,
        uint256 amountForBuyInstantly,
        uint24 toTreasuryRate,
        uint256 startTimestamp,
        uint256 deadline,
        string memory data
    ) public payable returns (IPresale) {
        IERC20(saleToken).safeTransferFrom(minter, address(this), amountToSale);
        return
            _create(
                minter,
                false,
                paymentToken,
                saleToken,
                sqrtPriceX96,
                tickLower,
                tickUpper,
                amountToSale,
                amountToRaise,
                amountForBuyInstantly,
                toTreasuryRate,
                startTimestamp,
                deadline,
                data
            );
    }

    function _create(
        address minter,
        bool isNewToken,
        address paymentToken,
        address saleToken,
        uint160 sqrtPriceX96,
        int24 tickLower,
        int24 tickUpper,
        uint256 amountToSale,
        uint256 amountToRaise,
        uint256 amountForBuyInstantly,
        uint24 toTreasuryRate,
        uint256 startTimestamp,
        uint256 deadline,
        string memory data
    ) internal returns (IPresale) {
        require(toTreasuryRate <= config.getMaxTreasuryRate(), "UniswapV3PresaleMaker: treasury rate too high");
        require(config.paymentTokenWhitelist(paymentToken), "UniswapV3PresaleMaker: payment token not whitelisted");
        require(amountToRaise > 0, "UniswapV3PresaleMaker: presale amount must be greater than 0");
        require(
            startTimestamp == 0 || startTimestamp >= block.timestamp,
            "UniswapV3PresaleMaker: start timestamp cannot be in the past"
        );
        // if sender is not whitelisted, minter can be different from msg.sender
        if (!config.isWhitelistedContract(msg.sender)) {
            require(minter == msg.sender, "UniswapV3PresaleMaker: minter must be msg.sender");
        }

        address _paymentToken = paymentToken;
        uint256 _mintingFee = config.mintingFee();
        if (eth == paymentToken) {
            require(msg.value >= amountForBuyInstantly + _mintingFee, "UniswapV3PresaleMaker: insufficient fees");
            _paymentToken = weth;
        } else {
            require(msg.value >= _mintingFee, "UniswapV3PresaleMaker: insufficient minting fee");

            if (amountForBuyInstantly > 0) {
                IERC20(paymentToken).safeTransferFrom(minter, address(this), amountForBuyInstantly);
            }
        }

        IERC20 tokenInstance = IERC20(saleToken);
        require(
            amountToSale > 0 && tokenInstance.balanceOf(address(this)) >= amountToSale,
            "UniswapV3PresaleMaker: invalid sale amount or insufficient balance"
        );

        (address token0, address token1) = paymentToken < saleToken
            ? (paymentToken, saleToken)
            : (saleToken, paymentToken);

        address pool = poolFactory.createPool(token0, token1, poolFee);
        IUniswapV3Pool(pool).initialize(sqrtPriceX96);

        IPresale.PresaleInfo memory info = IPresale.PresaleInfo({
            minter: msg.sender,
            token: saleToken,
            pool: pool,
            paymentToken: _paymentToken,
            amountToRaise: amountToRaise,
            amountToSale: amountToSale,
            data: data,
            toTreasuryRate: toTreasuryRate,
            isEnd: false,
            startTimestamp: startTimestamp == 0 ? block.timestamp : startTimestamp,
            isNewToken: isNewToken
        });

        ERC1967Proxy proxy = new ERC1967Proxy(
            address(implementation),
            abi.encodeCall(implementation.initialize, (swapRouter, positionManager, quoter, info, config))
        );
        IPresale presale = IPresale(address(proxy));

        tokenInstance.forceApprove(address(positionManager), amountToSale);
        (uint256 tokenId, uint128 liquidity, uint256 amount0, uint256 amount1) = positionManager.mint(
            INonfungiblePositionManager.MintParams({
                token0: token0,
                token1: token1,
                fee: poolFee,
                tickLower: tickLower,
                tickUpper: tickUpper,
                amount0Desired: token0 == saleToken ? amountToSale : 0,
                amount1Desired: token1 == saleToken ? amountToSale : 0,
                amount0Min: 0,
                amount1Min: 0,
                recipient: address(presale),
                deadline: deadline
            })
        );

        if (isNewToken) {
            CommonToken(saleToken).transferOwnership(address(presale));
        }

        presaleManager.register(presale);

        buyInstantly(minter, saleToken, paymentToken, amountForBuyInstantly, deadline);
        sendMintingFee(_mintingFee);

        return presale;
    }

    function sendMintingFee(uint256 _mintingFee) internal {
        if (_mintingFee > 0) {
            payable(config.feeVault()).call{value: _mintingFee}("");
        }
    }

    function buyInstantly(
        address minter,
        address token,
        address paymentToken,
        uint256 amount,
        uint256 deadline
    ) internal {
        if (amount == 0) {
            return;
        }
        uint256 value = (paymentToken == eth) ? amount : 0;
        if (paymentToken != eth) {
            // IERC20(paymentToken).safeTransferFrom(msg.sender, address(this), amount);
            IERC20(paymentToken).forceApprove(address(swapRouter), amount);
        }

        uint256 amountOut = swapRouter.exactInput{value: value}(
            ISwapRouter.ExactInputParams(
                abi.encodePacked(paymentToken == eth ? weth : paymentToken, poolFee, token),
                address(this),
                deadline,
                amount,
                0
            )
        );
        IERC20(token).safeTransfer(minter, amountOut);
    }
}

contracts/PresalePoolManager.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.8.7;

import {Configuration} from "./Configuration.sol";
import {PresaleManager} from "./presale-manager/PresaleManager.sol";
import {IPoolConfiguration} from "@kayen/uniswap-v3-core/contracts/interfaces/IPoolConfiguration.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IDistributor} from "./distributor/IDistributor.sol";

contract PresalePoolManager is IPoolConfiguration, Ownable {
    Configuration config;
    PresaleManager manager;
    address quoter;

    constructor(Configuration _config, PresaleManager _manager) Ownable() {
        config = _config;
        manager = _manager;
    }

    function getTradeFee(address token0, address token1) external view returns (uint24) {
        return config.getTradeFee(token0, token1);
    }

    function getFeeVault() external view returns (address) {
        return config.getFeeVault();
    }

    function beforeSwap(address pool, address recipient) external {
        if (recipient != quoter) {
            require(!checkIsPaused(pool), "PresalePoolManager: Pool is paused");
            require(!manager.isBondingCurveEnd(pool), "PresalePoolManager: Bonding curve end");
            require(!checkIsPending(pool), "PresalePoolManager: Pool is pending");
            require(!manager.isExpired(pool), "PresalePoolManager: Expired");
        }
    }

    function afterSwap(address pool, uint256 deadline) external {
        if (!manager.isBondingCurveEnd(pool)) {
            return;
        }
        address distributor = config.defaultDistributor();
        if (distributor == address(0)) {
            return;
        }
        if (manager.getPresale(pool).canDistribute(distributor)) {
            manager.getPresale(pool).distribute(distributor, deadline);
        }
    }

    function isWhitelistedMaker(address maker) external view returns (bool) {
        return config.presaleMakers(maker);
    }

    function putQuoter(address _quoter) external onlyOwner {
        quoter = _quoter;
    }

    function checkIsPending(address pool) private view returns (bool) {
        return manager.isPending(pool);
    }

    function checkIsPaused(address pool) private view returns (bool) {
        return config.isPaused(pool);
    }
}
