pragma solidity >=0.8.7;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@kayen/token/contracts/IBlacklist.sol";
import "./distributor/IDistributor.sol";

contract Configuration is Ownable, IBlacklist {
    mapping(address => bool) public paymentTokenWhitelist;
    mapping(address => bool) public distributorWhitelist;
    mapping(address => bool) public whitelistedContract;
    IDistributor[] public distributors;
    mapping(address => bool) public presaleMakers;
    mapping(address => bool) public isPoolPaused;
    mapping(address => bool) public transferBlacklist;

    address public defaultDistributor;

    uint256 public maxPresaleDuration = 365 days;
    bool public isAllPoolPaused;

    address public feeVault;

    uint24 public defaultDistributionFeeRate = 1e6 / 100; // 0.01 = 1%
    mapping(address => uint24) public distributionFeeRate;

    // amount of payment token as minting fee
    uint256 public mintingFee = 0;

    uint24 public defaultTradeFee = 1e6 / 100; // 0.01 = 1%
    mapping(address => uint24) public tradeFee;

    uint24 public maxTreasuryRate = 8e5; // 80%

    constructor(address _feeVault) {
        feeVault = _feeVault;
    }

    function putPresaleMaker(address presaleMaker) external onlyOwner {
        presaleMakers[presaleMaker] = true;
    }

    function removePresaleMaker(address presaleMaker) external onlyOwner {
        presaleMakers[presaleMaker] = false;
    }

    function allowTokenForPayment(address token) external onlyOwner {
        paymentTokenWhitelist[token] = true;
    }

    function disallowTokenForPayment(address token) external onlyOwner {
        paymentTokenWhitelist[token] = false;
    }

    function allowWhitelistedContract(address _whitelistedContract) external onlyOwner {
        whitelistedContract[_whitelistedContract] = true;
    }

    function disallowWhitelistedContract(address _whitelistedContract) external onlyOwner {
        whitelistedContract[_whitelistedContract] = false;
    }

    function isWhitelistedContract(address _whitelistedContract) external view returns (bool) {
        return whitelistedContract[_whitelistedContract];
    }

    function allowDistributor(address distributor) external onlyOwner {
        distributorWhitelist[distributor] = true;
        distributors.push(IDistributor(distributor));
    }

    function disallowDistributor(address distributor) external onlyOwner {
        distributorWhitelist[distributor] = false;
        for (uint256 i = 0; i < distributors.length; i++) {
            if (address(distributors[i]) == distributor) {
                distributors[i] = distributors[distributors.length - 1];
                distributors.pop();
                break;
            }
        }
    }

    function isDistributorWhitelisted(address distributor) external view returns (bool) {
        return distributorWhitelist[distributor];
    }

    function putDefaultDistributionFeeRate(uint24 feeRate) external onlyOwner {
        // max 30%
        require(feeRate <= (1e6 / 100) * 30, "Configuration: default distribution fee rate must be less than 30%");
        defaultDistributionFeeRate = feeRate;

        emit DefaultDistributionFeeRateUpdated(feeRate);
    }

    function putDistributionFeeRateForToken(address token, uint24 feeRate) external onlyOwner {
        // max 30%
        require(feeRate <= (1e6 / 100) * 30, "Configuration: default distribution fee rate must be less than 30%");
        distributionFeeRate[token] = feeRate;

        emit DistributionFeeRateForTokenUpdated(token, feeRate);
    }

    function getDistributionFeeRate(address token0, address token1) external view returns (uint24) {
        uint24 fee0 = distributionFeeRate[token0];
        uint24 fee1 = distributionFeeRate[token1];
        if (fee0 == 0 && fee1 == 0) {
            return defaultDistributionFeeRate;
        }
        return fee0 > fee1 ? fee0 : fee1;
    }

    function putFeeVault(address vault) external onlyOwner {
        require(vault != address(0), "Configuration: fee vault cannot be zero address");
        feeVault = vault;

        emit FeeVaultUpdated(vault);
    }

    function getFeeVault() external view returns (address) {
        return feeVault;
    }

    function isPaused(address pool) external view returns (bool) {
        return isPoolPaused[pool] || isPausedAll();
    }

    function isPausedAll() public view returns (bool) {
        return isAllPoolPaused;
    }

    function pause(address pool) external onlyOwner {
        isPoolPaused[pool] = true;
    }

    function unpause(address pool) external onlyOwner {
        isPoolPaused[pool] = false;
    }

    function pauseAll() external onlyOwner {
        isAllPoolPaused = true;
    }

    function unpauseAll() external onlyOwner {
        isAllPoolPaused = false;
    }

    function putMintingFee(uint256 fee) external onlyOwner {
        // max 30%
        require(fee <= 1000e18, "Configuration: minting fee must be less than 1000");
        mintingFee = fee;

        emit MintingFeeUpdated(fee);
    }

    function putDefaultTradeFee(uint24 _tradeFee) external onlyOwner {
        assertTradeFee(_tradeFee);
        defaultTradeFee = _tradeFee;

        emit DefaultTradeFeeUpdated(_tradeFee);
    }

    function putTradeFeeForToken(address token, uint24 _tradeFee) external onlyOwner {
        assertTradeFee(_tradeFee);
        tradeFee[token] = _tradeFee;

        emit TradeFeeForTokenUpdated(token, _tradeFee);
    }

    function getTradeFee(address token0, address token1) external view returns (uint24) {
        uint24 fee0 = tradeFee[token0];
        uint24 fee1 = tradeFee[token1];
        if (fee0 == 0 && fee1 == 0) {
            return defaultTradeFee;
        }
        return fee0 > fee1 ? fee0 : fee1;
    }

    function putMaxTreasuryRate(uint24 rate) external onlyOwner {
        require(rate <= 1e6, "Configuration: max treasury rate must be less than 1e6");
        maxTreasuryRate = rate;

        emit MaxTreasuryRateUpdated(rate);
    }

    function getMaxTreasuryRate() external view returns (uint24) {
        return maxTreasuryRate;
    }

    function assertTradeFee(uint24 _tradeFee) internal view {
        // max 2%
        require(_tradeFee <= 1e6 / 50, "Configuration: trade fee must be less than 1%");
    }

    function assertDistributeFee(uint24 _tradeFee) internal view {
        // max 2%
        require(_tradeFee <= 1e6 / 50, "Configuration: trade fee must be less than 1%");
    }

    function putTransferBlacklist(address target) external onlyOwner {
        transferBlacklist[target] = true;
    }

    function putComputedTransferBlacklist(address token1, address token2) external {
        require(presaleMakers[msg.sender], "Configuration: FORBIDDEN");
        for (uint256 i = 0; i < distributors.length; i++) {
            transferBlacklist[distributors[i].getPoolAddress(token1, token2)] = true;
        }
    }

    function removeTransferBlacklist(address target) external onlyOwner {
        transferBlacklist[target] = false;
    }

    function isTransferBlacklisted(address target) external view returns (bool) {
        return transferBlacklist[target];
    }

    function putMaxPresaleDuration(uint256 duration) external onlyOwner {
        maxPresaleDuration = duration;

        emit MaxPresaleDurationUpdated(duration);
    }

    function putDefaultDistributor(address distributor) external onlyOwner {
        require(distributorWhitelist[distributor], "Configuration: distributor not whitelisted");
        defaultDistributor = distributor;

        emit DefaultDistributorUpdated(distributor);
    }

    event DefaultTradeFeeUpdated(uint24 fee);
    event TradeFeeForTokenUpdated(address token, uint24 fee);
    event MintingFeeUpdated(uint256 fee);
    event DefaultDistributionFeeRateUpdated(uint24 fee);
    event DistributionFeeRateForTokenUpdated(address token, uint24 fee);
    event MaxTreasuryRateUpdated(uint24 rate);
    event MaxPresaleDurationUpdated(uint256 duration);
    event DefaultDistributorUpdated(address distributor);
    event FeeVaultUpdated(address vault);
}
","keccak256":"0x5774ee370e8b49abbc5022eaf3757b71dd58acec20f23d9164f76297efe4657a","license":"GPL-3.0"},"contracts/distributor/Distributor.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.8.7;

import {IDistributor} from "./IDistributor.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Configuration} from "../Configuration.sol";

abstract contract Distributor is IDistributor {
    using SafeERC20 for IERC20;
    Configuration internal immutable config;

    constructor(Configuration _config) {
        config = _config;
    }

    function distribute(address token0, address token1, uint160 sqrtPriceX96, uint256 deadline) external override {
        _doDistribute(token0, token1, sqrtPriceX96, deadline);
        sendRestToVault(token0, token1);
    }

    function _doDistribute(address token0, address token1, uint160 sqrtPriceX96, uint256 deadline) internal virtual;

    function sendRestToVault(address token0, address token1) internal {
        IERC20(token0).safeTransfer(config.feeVault(), IERC20(token0).balanceOf(address(this)));
        IERC20(token1).safeTransfer(config.feeVault(), IERC20(token1).balanceOf(address(this)));
    }

}","keccak256":"0xcdf295a3e801d86c299b978b56b64f42d044a80aea42a68f4647698d7b1308f9","license":"GPL-3.0"},"contracts/distributor/IDistributor.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.8.7;

interface IDistributor {
    function distribute(address token0, address token1, uint160 sqrtPriceX96, uint256 deadline) external;
    function getPoolAddress(address token0, address token1) external returns (address);
    function canDistribute(address token0, address token1) external view returns (bool);
}","keccak256":"0x9eaa8804f17c115e28333ad5095e957a6f78e8be0988b928b6378407e779796a","license":"GPL-3.0"},"contracts/distributor/UniswapV3Distributor.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.8.7;

import {Configuration} from "../Configuration.sol";
import {Distributor} from "./Distributor.sol";

import {INonfungiblePositionManager} from "@kayen/uniswap-v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol";
import {UniswapV2Library} from "@kayen/uniswap-v2-periphery/contracts/libraries/UniswapV2Library.sol";
import {IUniswapV3Factory} from "@kayen/uniswap-v3-core/contracts/interfaces/IUniswapV3Factory.sol";
import {TickMath} from "@kayen/uniswap-v3-core/contracts/libraries/TickMath.sol";
import {IUniswapV3Pool} from "@kayen/uniswap-v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";

import {LiquidityAmounts} from "@kayen/uniswap-v3-periphery/contracts/libraries/LiquidityAmounts.sol";
import {TickMath} from "@kayen/uniswap-v3-core/contracts/libraries/TickMath.sol";

contract UniswapV3Distributor is Distributor {
    using SafeERC20 for IERC20;

    uint24 fee = 100;
    IUniswapV3Factory immutable factory;
    INonfungiblePositionManager immutable positionManager;

    constructor(
        Configuration _config,
        IUniswapV3Factory _factory,
        INonfungiblePositionManager _positionManager
    ) Distributor(_config) {
        factory = _factory;
        positionManager = _positionManager;
    }

    function getPoolAddress(address token0, address token1) external returns (address) {
        (address tokenA, address tokenB) = UniswapV2Library.sortTokens(token0, token1);
        if (factory.getPool(tokenA, tokenB, fee) == address(0)) {
            factory.createPool(token0, token1, fee);
        }
        return address(factory.getPool(tokenA, tokenB, fee));
    }

    function canDistribute(address token0, address token1) public view override returns (bool) {
        return true;
    }

    function _doDistribute(address token0, address token1, uint160 sqrtPriceX96, uint256 deadline) internal override {
        (address tokenA, address tokenB) = UniswapV2Library.sortTokens(token0, token1);

        uint256 tokenABalance = IERC20(tokenA).balanceOf(address(this));
        uint256 tokenBBalance = IERC20(tokenB).balanceOf(address(this));

        positionManager.createAndInitializePoolIfNecessary(tokenA, tokenB, fee, sqrtPriceX96);

        IERC20(tokenA).forceApprove(address(positionManager), tokenABalance);
        IERC20(tokenB).forceApprove(address(positionManager), tokenBBalance);

        int24 tickSpacing = 1;
        int24 tickLower = TickMath.MIN_TICK - (TickMath.MIN_TICK % tickSpacing);
        int24 tickUpper = TickMath.MAX_TICK - (TickMath.MAX_TICK % tickSpacing);

        positionManager.mint(
            INonfungiblePositionManager.MintParams({
                token0: tokenA,
                token1: tokenB,
                fee: fee,
                tickLower: tickLower,
                tickUpper: tickUpper,
                amount0Desired: tokenABalance,
                amount1Desired: tokenBBalance,
                amount0Min: 0,
                amount1Min: 0,
                recipient: address(this),
                deadline: deadline
            })
        );
    }
}
